function transpose(n,t,o){o=2===n&&9>=t?t+3:1===n&&t>9?t-9:2===n&&10>=t?t+2:2===n&&t>10?t-10:99,console.log("Instrument: "+n),console.log("Pitch: "+t),console.log("Final Note: "+o)}var instrument=0,pitch=0,finalnote=0;$("#instrument-list").change(function(n){return n=$(this).val(),console.log("Instrument: "+n),n}),$("#pitch-list").change(function(n){return n=$(this).val(),console.log("Pitch: "+n),n}),$("#transpose-button").click(function(){transpose(instrument,pitch,finalnote),console.log(finalnote)});